INSERT INTO Members (Level_idLevel, Member_type_idMember_type, Sname, Name, Fname, webpage, email, tel, ShortCV) VALUES(2,1,'Αλεξίου','Αγγελική','Χρήστος','https://www.unipi.gr/unipi/el/alexiou.html','alexiou@unipi.gr','+30 210 4142761','Η Αγγελική Αλεξίου έλαβε το δίπλωμα ηλεκτρολόγου μηχανικού και μηχανικού υπολογιστών από το Εθνικό Μετσόβιο Πολυτεχνείο το 1994 και το διδακτορικό δίπλωμα ηλεκτρολόγου μηχανικού στην περιοχή των τηλεπικοινωνιών και της επεξεργασίας σήματος από το Imperial College, University of London, το 2000. Από το Μάρτιο του 2019 είναι καθηγήτρια με γνωστικό αντικείμενο Συστήματα Ευρυζωνικών Επικοινωνιών στο τμήμα Ψηφιακών Συστημάτων του Πανεπιστημίου Πειραιώς.')
INSERT INTO Members (Level_idLevel, Member_type_idMember_type, Sname, Name, Fname, webpage, email, tel, ShortCV) VALUES(2,1,'Βασιλακόπουλος','Γεώργιος','Μαρίνος','https://www.unipi.gr/unipi/el/gvass.html','gvass@unipi.gr','+30 210 4142370','Ο Γεώργιος Βασιλακόπουλος είναι διδάκτορας του Πανεπιστημίου του Λονδίνου, Καθηγητής του Τμήματος Ψηφιακών Συστημάτων του Πανεπιστημίου Πειραιώς και Αντιπρύτανης Ακαδημαϊκών Υποθέσεων και Προσωπικού του Πανεπιστημίου αυτού. Ως Αντιπρύτανης Ακαδημαϊκών Υποθέσεων, είναι, εκ του νόμου, Πρόεδρος της Μονάδας Διασφάλισης Ποιότητας (ΜΟΔΙΠ) και της Δομής Απασχόλησης και Σταδιοδρομίας (ΔΑΣΤΑ) του Πανεπιστημίου Πειραιώς, και έχει την επιστημονική/τεχνική ευθύνη των διαδικασιών ανάπτυξης αντίστοιχων σύγχρονων πληροφοριακών συστημάτων. Επί σειρά ετών, έχει διατελέσει Πρόεδρος του Τμήματος Ψηφιακών Συστημάτων, Διευθυντής Προγράμματος Μεταπτυχιακών Σπουδών και Επιστημονικός Υπεύθυνος του Εργαστηρίου Ψηφιακών Υπηρεσιών Υγείας του Τμήματος αυτού.')
