INSERT INTO Members (Level_idLevel, Member_type_idMember_type, Sname, Name, Fname, webpage, email, tel, ShortCV) VALUES(2,1,'Κάτσικας','Σωκράτης','Απόστολος','https://www.unipi.gr/unipi/el/ska.html','ska@unipi.gr','+30 210 4142757','Ο Σωκράτης Κ. Κάτσικας γεννήθηκε στην Αθήνα το 1960. Είναι Διπλωματούχος Ηλεκτρολόγος Μηχανικός του Πανεπιστημίου Πατρών (1982), Master of Science in Electrical & Computer Engineering του University of Massachusetts at Amherst, ΗΠΑ (1984) και Δρ Μηχ/κός Η/Υ & Πληροφορικής του Πανεπιστημίου Πατρών (1987)')
