INSERT INTO Members (Level_idLevel, Member_type_idMember_type, Sname, Name, Fname, webpage, email, tel, ShortCV) VALUES(2,1,'Ξενάκης','Χρήστος','Γεώργιος','https://www.unipi.gr/unipi/el/xenakis.html','xenakis@unipi.gr','+30 210 4142776','Ο Καθ. Χρήστος Ξενάκης ολοκλήρωσε τις σπουδές του στην Πληροφορική το 1993, και μεταπτυχιακά (M.Sc.) το 1996 στο Τμήμα Πληροφορικής και Τηλεπικοινωνιών του  Πανεπιστημίου Αθηνών. Το 2004 έλαβε το Διδακτορικό δίπλωμα (Ph.D) από το Πανεπιστήμιο  Αθηνών (Τμήμα Πληροφορικής και Τηλεπικοινωνιών). Το διάστημα 1998  2001 εργάστηκε ως μηχανικός τηλεπικοινωνιών. Από το 1996 έως 2007 ήταν μέλος του Εργαστηρίου Δικτύων  Επικοινωνιών του Τμήματος Πληροφορικής και Τηλεπικοινωνιών (Πανεπιστήμιο Αθηνών).  Από το 2007 ανήκει στο Διδακτικό Ερευνητικό Προσωπικό του Τμήματος Ψηφιακών Συστημάτων του Πανεπιστημίου Πειραιώς, όπου σήμερα είναι Καθηγητής.')
INSERT INTO Members (Level_idLevel, Member_type_idMember_type, Sname, Name, Fname, webpage, email, tel, ShortCV) VALUES(2,1,'Πρέντζα','Ανδριάνα','Χρήστος','https://www.unipi.gr/unipi/el/aprentza.html','aprentza@unipi.gr','+30 210 4142768','Η Ανδριάνα A. Πρέντζα σπούδασε Μηχανικός Ηλεκτρονικών Υπολογιστών & Πληροφορικής στο Πανεπιστήμιο Πατρών και στη συνέχεια απέκτησε Μεταπτυχιακό Δίπλωμα Ειδίκευσης στη Βιοϊατρική Τεχνολογία από το Διατμηματικό Πρόγραμμα Μεταπτυχιακών Σπουδών στη Βιοϊατρική Τεχνολογία του ΕΜΠ και του Πανεπιστημίου Πατρών και Διδακτορικό Δίπλωμα στη Βιοϊατρική Τεχνολογία από το Πολυτεχνείο Eindhoven, στην Ολλανδία. ')
