INSERT INTO Members (Level_idLevel, Member_type_idMember_type, Sname, Name, Fname, webpage, email, tel, ShortCV) VALUES(3,1,'Κυριαζής','Δημοσθένης','Ιωάννης','https://www.unipi.gr/unipi/el/dimos.html','dimos@unipi.gr','+30 210 414 2000','Ο Δημοσθένης Κυριαζής έλαβε το δίπλωμά του από τη Σχολή Ηλεκτρολόγων Μηχανικών και Μηχανικών Υπολογιστών του Εθνικού Μετσόβιου Πολυτεχνείου (ΕΜΠ) το 2001 και το διατμηματικό μεταπτυχιακό δίπλωμα σπουδών «Τεχνο-οικονομικά Συστήματα» το 2004 (σχολή Ηλεκτρολόγων Μηχανικών και Μηχανικών Υπολογιστών  ΕΜΠ, τμήμα Οικονομικών Επιστημών  Εθνικό Καποδιστριακό Πανεπιστήμιο Αθηνών, τμήμα Τεχνολογίας και Συστημάτων Παραγωγής  Πανεπιστήμιο Πειραιά)')
INSERT INTO Members (Level_idLevel, Member_type_idMember_type, Sname, Name, Fname, webpage, email, tel, ShortCV) VALUES(3,1,'Μαγκλογιάννης','Ηλίας','Απόστολος','https://www.unipi.gr/unipi/el/imaglo.html','imaglo@unipi.gr','+30 210 4142517','Ο Δρ. Η. Μαγκλογιάννης είναι διευθυντής στο Εργαστήριο Υπολογιστικής Βιοϊατρικής στο Τμήμα Ψηφιακών Συστημάτων του Πανεπιστημίου Πειραιά. Αποφοίτησε από το τμήμα Ηλεκτρολόγων Μηχανικών και Μηχανικών Υπολογιστών του ΕΜΠ και από το ίδιο τμήμα ανακηρύχθηκε Διδάκτορας Μηχανικός  με εξειδίκευση στη Βιοϊατρική Τεχνολογία και την Ιατρική Πληροφορική')
